mapper.select:
select

from system_account


		and group_id = #{groupId,jdbcType=INTEGER}


		and account = #{username,jdbcType=VARCHAR}


		and password = #{password,jdbcType=VARCHAR}


		and act_status = #{actStatus,jdbcType=VARCHAR}
	
	
		and salt = #{salt,jdbcType=VARCHAR}

mapper.insert:
insert into system_account (id, group_id, account,
password, act_status, crt_time,salt
)
values (#{id,jdbcType=INTEGER}, #{groupId,jdbcType=INTEGER},
#{username,jdbcType=VARCHAR},
#{password,jdbcType=VARCHAR}, #{actStatus,jdbcType=VARCHAR}, #{crtTime,jdbcType=TIMESTAMP}, #{salt,jdbcType=VARCHAR}
)
