mapper.select:
		select
		
		from `dailybuyer_countrysalesreports`
		where 1=1
		
			and report_date >= #{startDate}
		
		
       and report_date <= #{endDate}
		
		
			and platform = #{platform,jdbcType=VARCHAR}
		
		
			and buyer_country = #{buyerCountry,jdbcType=VARCHAR}
		
		ORDER BY `report_date` DESC

	select
	
	from `dailybuyer_countrysalesreports`
	where platform like 'new%' and platform != 'new_newegg'
	
		and platform = #{platform,jdbcType=VARCHAR}
	
	
		and report_date >= #{startDate,jdbcType=DATE}
	
	
    and report_date <= #{endDate,jdbcType=DATE}
	
	
		and buyer_country = #{buyerCountry,jdbcType=VARCHAR}
	
	ORDER BY `report_date` DESC

SELECT `buyer_country` FROM `dailybuyer_countrysalesreports` WHERE 1=1
and platform = #{platform,jdbcType=VARCHAR}
GROUP by `buyer_country`

SELECT `buyer_country` FROM `dailybuyer_countrysalesreports` where platform like 'new%' and platform != 'new_newegg'
and platform = #{platform,jdbcType=VARCHAR}
GROUP by `buyer_country`
