mapper.select:
select 

from dailyoutebaygroupleaderreprots
where zhandian = #{zhandian,jdbcType=VARCHAR}
  and report_date = #{reportDate,jdbcType=DATE}
  and groupleader = #{groupleader,jdbcType=VARCHAR}

select 

from dailyoutebaygroupleaderreprots
where 1=1

	and zhandian = #{zhandian,jdbcType=VARCHAR}


	and report_date >= #{startDate,jdbcType=VARCHAR} 


	and report_date <= #{endDate,jdbcType=VARCHAR}


	and groupleader = #{groupleader,jdbcType=VARCHAR}

    select YEARWEEK(report_date,1) startDate,groupleader,zhandian,sum(salesorder_num) salesorder_num,sum(order_num) order_num,sum(salesorder_total) salesorder_total,sum(product_total_cny)product_total_cny,sum(net_profit)net_profit
	from dailyoutebaygroupleaderreprots
	where 1=1
    
    	and zhandian = #{zhandian,jdbcType=VARCHAR}
    
    
    	and report_date >= #{startDate,jdbcType=VARCHAR} 
    
    
    	and report_date <= #{endDate,jdbcType=VARCHAR}
    
    
    	and groupleader = #{groupleader,jdbcType=VARCHAR}
    
    GROUP BY startDate,groupleader,zhandian

mapper.delete:
delete from dailyoutebaygroupleaderreprots
where zhandian = #{zhandian,jdbcType=VARCHAR}
  and report_date = #{reportDate,jdbcType=DATE}
  and groupleader = #{groupleader,jdbcType=VARCHAR}
