mapper.select:
select 

from dailyoutmanagerhuizongreprots
where platform = #{platform,jdbcType=VARCHAR}
  and report_date = #{reportDate,jdbcType=DATE}
  and manager = #{manager,jdbcType=VARCHAR}

select 

from dailyoutmanagerhuizongreprots
where 1=1

	and platform = #{platform,jdbcType=VARCHAR}


	and report_date between #{startDate,jdbcType=DATE} and #{endDate,jdbcType=DATE}


	and manager = #{manager,jdbcType=VARCHAR}

    select manager,YEARWEEK(report_date,1) startDate,sum(salesorder_num) salesorder_num,sum(order_num) order_num,sum(salesorder_total) salesorder_total,sum(product_total_cny)product_total_cny,sum(net_profit)net_profit
	from dailyoutmanagerhuizongreprots  
    where 1=1
    
    	and platform = #{platform,jdbcType=VARCHAR}
    
    
    	and report_date >= #{startDate,jdbcType=VARCHAR} 
    
    
    	and report_date <= #{endDate,jdbcType=VARCHAR}
    
    
    	and manager = #{manager,jdbcType=VARCHAR}
    
    GROUP BY manager,startDate

mapper.delete:
delete from dailyoutmanagerhuizongreprots
where platform = #{platform,jdbcType=VARCHAR}
  and report_date = #{reportDate,jdbcType=DATE}
  and manager = #{manager,jdbcType=VARCHAR}
