mapper.select:
  	select platform,report_date,order_num,product_total_cny,net_profit,net_profit_margin from dailyoutreprots
	where platform = 'a_ll'
	
       and report_date >= #{startDate}
    
    
       and report_date <= #{endDate}
    
	ORDER BY report_date

select platform,report_date,order_num,product_total_cny,net_profit,net_profit_margin from dailyoutreprots where platform != 'a_ll' and platform != ''
and report_date = #{reportDate}
ORDER BY report_date

select 

from dailyoutreprots
where

	
		platform like 'new%'
	
	
		platform = #{platform,jdbcType=VARCHAR}
	



	and report_date >= #{startDate,jdbcType=VARCHAR} 


	and report_date <= #{endDate,jdbcType=VARCHAR}

select 

from dailyoutreprots
where platform = #{platform,jdbcType=VARCHAR}
and report_date = #{reportDate,jdbcType=DATE}

mapper.delete:
delete from dailyoutreprots
where platform = #{platform,jdbcType=VARCHAR}
  and report_date = #{reportDate,jdbcType=DATE}
