mapper.select:
  	select `platform`, `sales_account`, `sku`, `report_date`, `manager`, `order_num`, 
		  	`unit_price`, `product_total_cny`, `product_refund`, `order_price`, `gross_profit`,
		  	 `product_shipping`, `platform_cost`, `material_cost`,
		  	 `order_execution_fee`, `operating_cost`, `profit_margin`, `net_profit`
  	from dailyoutskureprots_new
	where 1=1
	
       and report_date >= #{startDate}
    
    
       and report_date <= #{endDate}
    
    
       and platform = #{platform,jdbcType=VARCHAR}
    
    
       and sales_account = #{salesAccount,jdbcType=VARCHAR}
    
    
    
       and sku = #{sku,jdbcType=VARCHAR}
