mapper.select:
select

from dailysalesreports
where report_date = #{reportDate,jdbcType=DATE}
and business = #{business,jdbcType=VARCHAR}

		select
		business, sum(orders) orders, sum(sales) sales
		from dailysalesreports
		where 1=1
		
			
				and business = #{business,jdbcType=VARCHAR}
			
			
				and (business like 'cn_%'
				or business ='ebay')
			
		
		
    			and report_date >= #{startDate,jdbcType=DATE}
		
		
	  		 	 and reportDate <= #{endDate,jdbcType=DATE}
		
		GROUP BY business

select

from dailysalesreports
where 1=1

	and business = #{business,jdbcType=VARCHAR}


	and report_date >= #{startDate,jdbcType=DATE}


	    and report_date <= #{endDate,jdbcType=DATE}

order by business,report_date

	select
	
	from dailysalesreports
	where business like 'new%' and business != 'new_newegg'
	
		and business = #{business,jdbcType=VARCHAR}
	
	
		and report_date >= #{startDate,jdbcType=DATE}
	
	
    and report_date <= #{endDate,jdbcType=DATE}
	
	order by report_date

mapper.delete:
delete from dailysalesreports
where report_date = #{reportDate,jdbcType=DATE}
and business = #{business,jdbcType=VARCHAR}

mapper.update:
update dailysalesreports


		orders = #{orders,jdbcType=INTEGER},


		sales = #{sales,jdbcType=REAL},


where report_date = #{reportDate,jdbcType=DATE}
and business = #{business,jdbcType=VARCHAR}

update dailysalesreports
set orders = #{orders,jdbcType=INTEGER},
sales = #{sales,jdbcType=REAL}
where report_date = #{reportDate,jdbcType=DATE}
and business = #{business,jdbcType=VARCHAR}
